import React from 'react';
import buttonLight from './buttonLight'
import ButtonLight from './buttonLight';
const CaptainCard = props =>{

    return( 
        <div className="ui link cards">
            <div class="card">
                <div class="image">
                <img src={props.captain.image}/>
                </div>
                <div class="content">
                    <div class="header">{props.captain.name}</div>
                    <div class="meta"><a>{'Seen in:' + props.captain.seenIn}</a></div>
                    <div class="description">
                        {props.captain.text}
                    </div>
                </div>
                <ButtonLight btnText = "Read More" />
            </div>
        </div>
    );
};

export default CaptainCard;
